import React from "react"

export default function Textarea(props) {
    const [textareaPosition, setTextareaPosition] = React.useState({
        posX:0,
        posY:0,
        dragging:false
    })

    function handleMouseDown(event){
        setTextareaPosition(privState=>({
            ...privState,
            dragging:true,
            posX:event.clientX,
            posY:event.clientY
        }))
    }

    function handleMouseUp(){
        setTextareaPosition(privState=>({
            ...privState,
            dragging:false
        }))
    }

    function handleMouseMove(event){
        if(textarea.dragging){
            const dx = e.clientX - posX;
            const dy = e.clientY - posY;
            setTextareaPosition(privState=>({
                ...privState,
                posX:event.clientX,
                posY:event.clientY
            }))
            e.target.style.top = `${e.target.offsetTop + dy}px`
            e.target.style.left = `${e.target.offsetLeft + dx}px`
        }

        return(
            <textarea
            style={{ position: 'absolute', top: `${posY}px`, left: `${posX}px` }}
            onMouseDown={handleMouseDown}
            onMouseUp={handleMouseUp}
            onMouseMove={handleMouseMove}
            />

        )
    }

}
