import React from "react";
import "./Form.css"

export default function Form(){

    const [memes, setMemes] = React.useState([]);

    React.useEffect(()=>{
        const fetchMemes = async ()=>{
            const response = await fetch('https://api.imgflip.com/get_memes')
            const data = await response.json()
            setMemes(data.data.memes)
        }
        fetchMemes()
    },[])

    const [randomMeme, setRandomMeme] = React.useState({
        topText: "",
        bottomText:"",
        randomImg: ""
    })

    function handleClick(){
        const randomIndex = Math.floor(Math.random()*memes.length)
        setRandomMeme(prevState=>( {...prevState, randomImg: memes[randomIndex].url}))
    }

    const[firstName, setFirstName] = React.useState("");

    function handleChange(event){
        console.log(firstName)
        setFirstName(event.target.value)

    }



    return(
        <div className="main"> 
        <input className="text-input" type="text" placeholder="top text" onChange={handleChange}></input>  
        <input className="text-input" type="text" placeholder="bottom text"></input>
        <button onClick={handleClick} className="button">generate new meme</button>
        <img className="meme" src={randomMeme.randomImg} />
        <textarea value={"hello"} className="top-text" readOnly></textarea>
        </div>

    )
}
